        private App GetApp(App request)
        {
            var id = request?.Id;
            App ret = null;
            var query = DocQuery.ActiveQuery ?? Execute;

            request.VisibleFields = InitVisibleFields<App>(Dto.App.Fields, request);

            DocEntityApp entity = null;
            if(id.HasValue)
            {
                entity = DocEntityApp.GetApp(id.Value);
            }
            if(null == entity)
                throw new HttpError(HttpStatusCode.NotFound, $"No App found for Id {id.Value}");

            if(!DocPermissionFactory.HasPermission(entity, currentUser, DocConstantPermission.VIEW))
                throw new HttpError(HttpStatusCode.Forbidden, "You do not have VIEW permission for this route.");
            
            ret = entity?.ToDto();
            return ret;
        }

