                    var pScopes = entity.Scopes.ToList();
                    var pStatus = entity.Status;
                    var pType = entity.Type;
                    var copy = new DocEntityBroadcast(ssn)
                    {
                        Hash = Guid.NewGuid()
                                , App = pApp
                                , ConfluenceId = pConfluenceId
                                , Name = pName
                                , Reprocess = pReprocess
