        private Broadcast GetBroadcast(Broadcast request)
        {
            var id = request?.Id;
            Broadcast ret = null;
            var query = DocQuery.ActiveQuery ?? Execute;

            DocPermissionFactory.SetSelect<Broadcast>(currentUser, "Broadcast", request.Select);

            DocEntityBroadcast entity = null;
            if(id.HasValue)
            {
                entity = DocEntityBroadcast.Get(id.Value);
            }
            if(null == entity)
                throw new HttpError(HttpStatusCode.NotFound, $"No Broadcast found for Id {id.Value}");

            if(!DocPermissionFactory.HasPermission(entity, currentUser, DocConstantPermission.VIEW))
                throw new HttpError(HttpStatusCode.Forbidden, "You do not have VIEW permission for this route.");
            
            ret = entity?.ToDto();
            return ret;
        }

