                #endregion Custom Before copyHelp
                var copy = new DocEntityHelp(ssn)
                {
                    Hash = Guid.NewGuid()
                                , ConfluenceId = pConfluenceId
                                , Description = pDescription
                                , Icon = pIcon
                                , Order = pOrder
                                , Title = pTitle
                                , Type = pType
                };
