            Locale ret = null;
            var query = DocQuery.ActiveQuery ?? Execute;

            DocPermissionFactory.SetVisibleFields<Locale>(currentUser, "Locale", request.VisibleFields);

            DocEntityLocale entity = null;
            if(id.HasValue)
            {
