        private Package GetPackage(Package request)
        {
            var id = request?.Id;
            Package ret = null;
            var query = DocQuery.ActiveQuery ?? Execute;

            request.VisibleFields = InitVisibleFields<Package>(Dto.Package.Fields, request);

            DocEntityPackage entity = null;
            if(id.HasValue)
            {
                entity = DocEntityPackage.GetPackage(id.Value);
            }
            if(null == entity)
                throw new HttpError(HttpStatusCode.NotFound, $"No Package found for Id {id.Value}");

            if(!DocPermissionFactory.HasPermission(entity, currentUser, DocConstantPermission.VIEW))
                throw new HttpError(HttpStatusCode.Forbidden, "You do not have VIEW permission for this route.");
            
            ret = entity?.ToDto();
            return ret;
        }

