            dtoSource.VisibleFields = dtoSource.VisibleFields ?? new List<string>();
            
            Page ret = null;
            Execute.Run(ssn =>
            {
                ret = _AssignValues(dtoSource, DocConstantPermission.EDIT, ssn);
            });
            return ret;
        }

