        private Role GetRole(Role request)
        {
            var id = request?.Id;
            Role ret = null;
            var query = DocQuery.ActiveQuery ?? Execute;

            DocPermissionFactory.SetVisibleFields<Role>(currentUser, "Role", request.VisibleFields);

            DocEntityRole entity = null;
            if(id.HasValue)
            {
                entity = DocEntityRole.GetRole(id.Value);
            }
            if(null == entity)
                throw new HttpError(HttpStatusCode.NotFound, $"No Role found for Id {id.Value}");

            if(!DocPermissionFactory.HasPermission(entity, currentUser, DocConstantPermission.VIEW))
                throw new HttpError(HttpStatusCode.Forbidden, "You do not have VIEW permission for this route.");
            
            ret = entity?.ToDto();
            return ret;
        }
    }
